import{_ as t,k as e,p as n,ac as r}from"./chunks/framework.C5AoO3Cs.js";const o="/assets/202302251677306154698973.DjzgF1xF.png",a="/assets/%E8%87%AA%E7%84%B6%E7%A0%81%E5%8A%A9%E8%AE%B0%E6%AD%8C.CmH7FfHM.png",p="/assets/202211071667815422312851.BnqXTCJX.png",s="/assets/image-20230330164314770.7T0E6OcO.png",z=JSON.parse('{"title":"音码学习","description":"","frontmatter":{},"headers":[],"relativePath":"hanxin/hanxinxuexi/yinma.md","filePath":"hanxin/hanxinxuexi/yinma.md"}'),g={name:"hanxin/hanxinxuexi/yinma.md"};function x(l,i,h,m,b,d){return n(),e("div",null,i[0]||(i[0]=[r('<h1 id="%E9%9F%B3%E7%A0%81%E5%AD%A6%E4%B9%A0" tabindex="-1">音码学习 <a class="header-anchor" href="#音码学习" aria-label="Permalink to &quot;音码学习&quot;">​</a></h1><p>需要你会双拼或全拼</p><p>如果会任何双拼,这里都不用看,你用你以前的就可以</p><p>因为汉心码支持其他双拼</p><p>汉心码,不是纯形(五笔类型),而是双拼(拼音两键打法)加汉心形码,或者拼音加汉心形码</p><p><a href="https://www.bilibili.com/video/BV1AC4y137WQ/" target="_blank" rel="noreferrer"><strong>📺视频版介绍巧妙学双拼的技巧</strong></a></p><h1 id="%E6%8E%A8%E8%8D%90%E5%AD%A6%E8%87%AA%E7%84%B6%E5%8F%8C%E6%8B%BC" tabindex="-1">推荐学自然双拼 <a class="header-anchor" href="#推荐学自然双拼" aria-label="Permalink to &quot;推荐学自然双拼&quot;">​</a></h1><p>汉心码目前适配自然双拼和小鹤双拼,其他双拼也可以.一键转码就可以.</p><p><strong>如果会任何双拼,这里都不用看,你用你以前的就可以</strong>用一键转码转一下就可以了</p><p><img src="'+o+'" alt="自然码双拼键位图"></p><p><strong>自然码双拼键位图</strong></p><p>这里应该都能看懂.双拼就是,把拼音分两个部分.第一个部分就是拼音首字母.第二个部分就是剩余的.剩余的全部安排在另一个键上.</p><p>用法举例:</p><p>全拼打chuang=窗</p><p>双拼打id=窗</p><p>i上有ch d上有uang 这就是双拼,一看就懂.很简单</p><p><strong>自然双拼怎么启用</strong></p><p>可以</p><p>1.手机设置里,选择自然双拼.电脑输入法,也是设置里选择双拼,然后选择自然双拼</p><p>或用</p><p>2.直接下载汉心快打输入法或汉心小狼毫输入法自然版的</p><p><img src="'+a+'" alt="自然码双拼助记歌顺口溜"></p><p><strong>自然码双拼助记歌顺口溜</strong></p><p>这个口诀不提倡背,但是如果你记忆力好.那可以背.</p><p><strong>建议手机下载讯飞输入法,皮肤里搜索找到自然的皮肤键盘,多多聊聊天就自然学会了</strong></p><h1 id="%E6%89%8B%E6%9C%BA%E4%B8%8A%E8%81%8A%E8%81%8A%E5%A4%A9%E8%BD%BB%E6%9D%BE%E5%AD%A6%E8%87%AA%E7%84%B6%E5%8F%8C%E6%8B%BC" tabindex="-1">手机上聊聊天轻松学自然双拼 <a class="header-anchor" href="#手机上聊聊天轻松学自然双拼" aria-label="Permalink to &quot;手机上聊聊天轻松学自然双拼&quot;">​</a></h1><p>步骤先下载,讯飞输入法，在皮肤里搜索,自然双拼,安装使用</p><p>每个键盘都有提示，非常实用.你聊聊天,多用用就会了</p><p><img src="'+p+'" alt="讯飞输入法自然码键盘皮肤"></p><p><strong>讯飞输入法自然码键盘皮肤</strong></p><h2 id="%E7%94%B5%E8%84%91%E7%AB%AF%E5%AD%A6%E4%B9%A0%E6%8E%A8%E8%8D%90%E5%90%AF%E7%94%A8%E7%9A%AE%E8%82%A4%E6%8F%90%E9%86%92%E5%AD%A6%E4%B9%A0" tabindex="-1">电脑端学习推荐启用皮肤提醒学习 <a class="header-anchor" href="#电脑端学习推荐启用皮肤提醒学习" aria-label="Permalink to &quot;电脑端学习推荐启用皮肤提醒学习&quot;">​</a></h2><p><img src="'+s+'" alt="电脑搜狗双拼键盘皮肤"></p><p><strong>电脑搜狗双拼键盘皮肤</strong></p><h2 id="%E7%94%B5%E8%84%91%E7%9A%AE%E8%82%A4%E5%90%AF%E7%94%A8%E6%96%B9%E6%B3%95" tabindex="-1">电脑皮肤启用方法 <a class="header-anchor" href="#电脑皮肤启用方法" aria-label="Permalink to &quot;电脑皮肤启用方法&quot;">​</a></h2><p><a href="https://www.123pan.com/s/rrSmjv-dh3g3.html" target="_blank" rel="noreferrer">https://www.123pan.com/s/rrSmjv-dh3g3.html</a></p><p>这里下载，先装里面的搜狗输入法的，然后点击皮肤。</p><p>如果链接失效，只能去网盘里下载。</p><h1 id="%E5%8F%AF%E9%80%89%E5%AD%A6%E9%9D%92%E6%9D%BE%E5%8F%8C%E6%8B%BC" tabindex="-1">可选学青松双拼 <a class="header-anchor" href="#可选学青松双拼" aria-label="Permalink to &quot;可选学青松双拼&quot;">​</a></h1><details style="box-sizing:inherit;display:block;color:rgb(52, 73, 94);font-family:-apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen-Sans, Ubuntu, Cantarell, &quot;Helvetica Neue&quot;, sans-serif;font-size:15px;font-style:normal;font-variant-ligatures:normal;font-variant-caps:normal;font-weight:400;letter-spacing:normal;orphans:2;text-align:start;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;-webkit-text-stroke-width:0px;background-color:rgb(255, 255, 255);text-decoration-style:initial;text-decoration-color:initial;"><summary style="box-sizing:inherit;display:list-item;">展开查看</summary><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">如果会任何双拼,这里都不用看,你用你以前的就可以</strong></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><em style="box-sizing:inherit;font-style:italic;"><strong style="box-sizing:inherit;font-weight:bold;">*因为汉心码支持其他双拼*</strong></em></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/202302251677306473860604.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/202302251677306473860604.png" alt="image.png" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">image.png</b></div></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">这个容易记,需要在双拼设置里自定义双拼方案.自己把键位自定义上.如果觉得麻烦就用自然双拼</p></details><h1 id="%E5%8F%AF%E9%80%89%E5%AD%A6%E5%85%B6%E4%BB%96%E5%8F%8C%E6%8B%BC" tabindex="-1">可选学其他双拼 <a class="header-anchor" href="#可选学其他双拼" aria-label="Permalink to &quot;可选学其他双拼&quot;">​</a></h1><details style="box-sizing:inherit;display:block;color:rgb(52, 73, 94);font-family:-apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen-Sans, Ubuntu, Cantarell, &quot;Helvetica Neue&quot;, sans-serif;font-size:15px;font-style:normal;font-variant-ligatures:normal;font-variant-caps:normal;font-weight:400;letter-spacing:normal;orphans:2;text-align:start;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;-webkit-text-stroke-width:0px;background-color:rgb(255, 255, 255);text-decoration-style:initial;text-decoration-color:initial;"><summary style="box-sizing:inherit;display:list-item;">展开查看</summary><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">如果会任何双拼,这里都不用看,你用你以前的就可以</strong></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">因为汉心码支持其他双拼</strong></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/202302251677306629656123.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/202302251677306629656123.png" alt="输入法里选择双拼" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">输入法里选择双拼</b></div></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">优先建议自然或小鹤，因为这两个汉心默认支持，其他的需要自己转码。</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/xnheulpb.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/xnheulpb.png" alt="小鹤双拼键位图" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">小鹤双拼键位图</b></div></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">想学小鹤的可以按照这个图学,</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">口诀,还是不建议背,可以手机上讯飞输入法搜索小鹤键盘皮肤,打打就会了</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/xnhekzjt.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/xnhekzjt.png" alt="小鹤双拼口诀" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">小鹤双拼口诀</b></div></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">你说有什么区别吗？区别不太大。</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">手感最好的是非乱序双拼是大牛双拼.但是可能很多输入法没有内置,需要你自己自定义.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">然后据说蓝天手感也很好</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">但是手感好的双拼他们装机量就少,很多输入法里没有,所以一般人也不选他们.</strong></p><hr style="box-sizing:content-box;height:0px;overflow:visible;margin:20px 0px;border-width:1px 0px 0px;border-right-style:initial;border-bottom-style:initial;border-left-style:initial;border-right-color:initial;border-bottom-color:initial;border-left-color:initial;border-image:initial;border-top-style:solid;border-top-color:rgb(204, 204, 204);"><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">目前主流就两个,自然和小鹤</strong></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">微软双拼 搜狗双拼 基本和自然双拼一样 基本就1个键差别</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">自然码双拼的好处的.所有输入法都内置,微软输入法也内置</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">所以学他们任意一个就等于学了三个</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">特别一些考公考编的地方.不能装输入法.只能用系统里的,系统里默认会有微软双拼和自然双拼.但是没有小鹤双拼.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">小鹤双拼,很多输入法也都有内置,只有微软没有内置，手感紧凑一点.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">自然双拼手感大开大合一点.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">手感,见仁见智.两个在手感上没有明显的差距,当你用熟了以后,感觉都差不多.每个都有别手的组合,只是各个都不一样.这两个互转都很容易,因为他们大部分键位也是一样的.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">自然码.普适性通用性最好.你用别人电脑也都可以很快可以切换过去用.</strong></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><strong style="box-sizing:inherit;font-weight:bold;">所以一般你如果拿不准,优先推荐自然码双拼</strong>,你如果倾向喜欢小鹤,选小鹤也挺好.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">汉心码,对两者都默认适配音形输入法.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">当然你追求极致手感,那就不要选上述两个.</p><h2 id="如果学其他双拼可以用我们的皮肤来辅助学习" style="box-sizing:inherit;font-weight:bold;line-height:1.8rem;font-size:1.8rem;margin:42px 0px 18px -5px;"><a href="https://hanxinma.gitlab.io/2023/08/30/xue-shuang-pin/#如果学其他双拼可以用我们的皮肤来辅助学习" class="headerlink" title="如果学其他双拼可以用我们的皮肤来辅助学习" target="_blank" style="box-sizing:inherit;background-color:transparent;color:rgb(0, 119, 59);text-decoration:none;-webkit-tap-highlight-color:transparent;font-weight:500;margin-right:0px !important;text-transform:none !important;padding:0px 2px;overflow-wrap:break-word;"></a>如果学其他双拼可以用我们的皮肤来辅助学习</h2><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/image-20230330164314770.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/image-20230330164314770.png" alt="image-20230330164314770" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">image-20230330164314770</b></div></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/image-20230330164124864.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/image-20230330164124864.png" alt="image-20230330164124864" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">image-20230330164124864</b></div></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/image-20230330164155148.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/image-20230330164155148.png" alt="image-20230330164155148" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"><div class="caption" style="box-sizing:inherit;text-align:center;margin:0px auto 15px;"><b class="center-caption" style="box-sizing:inherit;font-weight:bold;color:rgb(82, 95, 127);padding:5px;border-bottom:1px solid rgb(217, 217, 217);">image-20230330164155148</b></div></div><p></p><h2 id="上述皮肤启用" style="box-sizing:inherit;font-weight:bold;line-height:1.8rem;font-size:1.8rem;margin:42px 0px 18px -5px;"><a href="https://hanxinma.gitlab.io/2023/08/30/xue-shuang-pin/#上述皮肤启用" class="headerlink" title="上述皮肤启用" target="_blank" style="box-sizing:inherit;background-color:transparent;color:rgb(0, 119, 59);text-decoration:none;-webkit-tap-highlight-color:transparent;font-weight:500;margin-right:0px !important;text-transform:none !important;padding:0px 2px;overflow-wrap:break-word;"></a>上述皮肤启用</h2><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"><a target="_blank" rel="noopener" href="https://www.123pan.com/s/rrSmjv-dh3g3.html" style="box-sizing:inherit;background-color:transparent;color:rgb(0, 119, 59);text-decoration:none;-webkit-tap-highlight-color:transparent;margin-right:0px !important;text-transform:none !important;padding:0px 2px;font-weight:500;overflow-wrap:break-word;">123网盘内下载搜狗和双拼提示皮肤</a></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">这里下载，先装里面的搜狗输入法的，然后点击皮肤。</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">如果链接失效，只能去网盘里下载。</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">网盘里没有老版的搜狗。需要你自己百度。</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">新版搜狗，输入法皮肤，没法点皮肤就切换。必须，你把皮肤复制到相关目录里。然后在设置里选择对应的皮肤才可以用。</p></details><h1 id="%E5%A6%82%E6%9E%9C%E4%BD%A0%E8%AF%B4%E6%88%91%E4%B8%8D%E6%83%B3%E5%AD%A6%E5%8F%8C%E6%8B%BC%2C%E9%82%A3%E5%8F%AF%E4%BB%A5%E7%9B%B4%E6%8E%A5%E7%94%A8%E6%99%AE%E9%80%9A%E6%8B%BC%E9%9F%B3" tabindex="-1">如果你说我不想学双拼,那可以直接用普通拼音 <a class="header-anchor" href="#如果你说我不想学双拼%2C那可以直接用普通拼音" aria-label="Permalink to &quot;如果你说我不想学双拼,那可以直接用普通拼音&quot;">​</a></h1><p>汉心码支持普通拼音,所以你也可以不用学双拼.但是推荐学双拼,受益终身.</p><p><a href="./../quanpin/xuexi.html"><strong>拼音用户点这里跳转</strong></a></p>',44)]))}const y=t(g,[["render",x]]);export{z as __pageData,y as default};
