import{_ as e,h as t,a9 as r,f as p}from"./chunks/framework.B8UBlkKL.js";const i="/assets/yuanliyanshi.Co7hWIno.gif",B=JSON.parse('{"title":"汉心码介绍","description":"","frontmatter":{"title":"汉心码介绍"},"headers":[],"relativePath":"hanxin/index.md","filePath":"hanxin/index.md"}'),n={name:"hanxin/index.md"};function o(l,a,E,s,h,d){return p(),t("div",null,a[0]||(a[0]=[r('<p><br><br></p><p align="center" style="font-size:34px;font-weight:bold;">汉心码官网</p><br><br><br><h2 id="%E6%B1%89%E5%BF%83%E7%A0%81%E6%98%AF%E6%8B%BC%E9%9F%B3%E6%88%96%E5%8F%8C%E6%8B%BC%E6%89%93%E5%AD%97%E8%AF%8D%E9%83%BD%E4%B8%8D%E7%94%A8%E9%80%89%E7%9A%84%E8%BE%85%E5%8A%A9%E7%A0%81%E3%80%82" tabindex="-1">汉心码是拼音或双拼打字词都不用选的辅助码。 <a class="header-anchor" href="#汉心码是拼音或双拼打字词都不用选的辅助码。" aria-label="Permalink to &quot;汉心码是拼音或双拼打字词都不用选的辅助码。&quot;">​</a></h2><p><img src="'+i+'" alt="img"></p><p>通俗的说，就是，拼音打字不用翻页找字的方法。</p><h2 id="%E6%B1%89%E5%BF%83%E7%A0%81%E5%8E%9F%E7%90%86" tabindex="-1">汉心码原理 <a class="header-anchor" href="#汉心码原理" aria-label="Permalink to &quot;汉心码原理&quot;">​</a></h2><hr><p>汉心码是用拼音加汉字的偏旁部首的读音来做到不选字。</p><p>【比如“钿”字】</p><p>只要打“tian”加“钅”字旁首字母“j”就不用翻页选字。</p><p>所以汉心码非常简单易学。</p><p>辅助码是配合双拼或全拼使用的，所以你必须会双拼或全拼，全拼既是拼音。</p><h2 id="%E6%B1%89%E5%BF%83%E7%A0%81%E7%89%B9%E7%82%B9" tabindex="-1">汉心码特点 <a class="header-anchor" href="#汉心码特点" aria-label="Permalink to &quot;汉心码特点&quot;">​</a></h2><hr><p>1.冷僻字根更少,不用费心记忆字根,看一遍,就能学会。</p><p>2.整体也挺简单,所以长期不怎么打字,也不容易忘记。（重要）</p><p>3.在搜狗输入法里也可以直接查字的汉心码编码。</p><p>4.重码低,还能先打尾码,对新手更友好。</p><p>学过很多辅助码用户反馈：辅助码尽量选简单的， 过于繁杂字根多的方案。打字少容易忘记字根。</p><p>对于普通用户，辅助码不建议入手相对复杂的。 因为用的少易忘，学习性价比不高。</p><h2 id="%E4%B8%8B%E8%BD%BD" tabindex="-1">下载 <a class="header-anchor" href="#下载" aria-label="Permalink to &quot;下载&quot;">​</a></h2><hr><p>汉心码-是码表,（码表分全拼版和双拼版）。</p><p>码表可以挂接导入到搜狗手心百度等输入法里使用。</p><p>汉心快打-是软件（软件主要有两种，快打版和小狼毫版）。</p><p id="xiazai">汉心快打是整合了汉心码，拥有更多功能的输入法软件。一键安装方便快捷。</p><ul><li>访问 <a href="http://yaoxiazai.ysepan.com/" target="_blank" rel="noreferrer">汉心网盘</a></li></ul>',30)]))}const f=e(n,[["render",o]]);export{B as __pageData,f as default};
