import{_ as e,h as t,a9 as n,f as p}from"./chunks/framework.B8UBlkKL.js";const l=JSON.parse('{"title":"导入手心的辅助码程序中使用方法","description":"","frontmatter":{},"headers":[],"relativePath":"hanxin/shiyongfangfa/daoru.md","filePath":"hanxin/shiyongfangfa/daoru.md"}'),r={name:"hanxin/shiyongfangfa/daoru.md"};function a(s,i,x,g,o,m){return p(),t("div",null,i[0]||(i[0]=[n('<h3 id="%E7%AC%AC%E4%B8%80%E7%A7%8D%E6%98%AF%E6%8C%82%E7%A0%81%E8%A1%A8%E5%88%B0%E5%85%B6%E4%BB%96%E8%BE%93%E5%85%A5%E6%B3%95%E7%9F%AD%E8%AF%AD%E9%87%8C" tabindex="-1"><strong>第一种是挂码表到其他输入法短语里</strong> <a class="header-anchor" href="#第一种是挂码表到其他输入法短语里" aria-label="Permalink to &quot;**第一种是挂码表到其他输入法短语里**&quot;">​</a></h3><p>这个好处是,你可以打句子,和原来打拼音的使用习惯基本一样,自由度高</p><p>[推荐用这种]</p><p>第一种,<a href="https://www.ixigua.com/7231104568191353344?logTag=770a29f1485f9699eece" target="_blank" rel="noreferrer">点我跳转查看详细方法</a></p><p>如果视频失效就看文字教程</p><p>挂码表的方法就是,你首先打开[输入法设置],在里面找到自定义短语,搜狗的自定义短语在设置-高级里,然后点直接编辑,把码表复制进去就可以了,其他输入法如果有导入可以点击导入也是可以的</p><h1 id="%E5%AF%BC%E5%85%A5%E6%89%8B%E5%BF%83%E7%9A%84%E8%BE%85%E5%8A%A9%E7%A0%81%E7%A8%8B%E5%BA%8F%E4%B8%AD%E4%BD%BF%E7%94%A8%E6%96%B9%E6%B3%95" tabindex="-1">导入手心的辅助码程序中使用方法 <a class="header-anchor" href="#导入手心的辅助码程序中使用方法" aria-label="Permalink to &quot;导入手心的辅助码程序中使用方法&quot;">​</a></h1><details open="" style="box-sizing:inherit;display:block;"><summary style="box-sizing:inherit;display:list-item;">展开查看</summary><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">手心专业辅助码的好处是可以对词语辅助</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">辅助码程序中的坏处是,字顺序每次都不一样</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">辅助码字优先,可能你要打词,结果出字</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">所以一般不建议用辅助码,建议挂</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">要用的话,建议就导入一位辅助码,这样不干扰词,还能辅词</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">.</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">这个就要用手心输入法了,或其他有导入辅助码功能的输入法。一般的用不了。</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">电脑手机是一样的</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">都要安装手心输入法,百度找一下.或者市场里找一下</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">手心安装好了.这设置里.双拼设置里.辅助码里导入即可</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/202302251677317585529678.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/202302251677317585529678.png" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/202302251677317601330652.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/202302251677317601330652.png" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">然后把这样格式的导入到手心里就可以用了</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;"></p><div class="img-item" data-src="/medias/img/202302251677317640784456.png" data-sub-html=".caption" style="box-sizing:inherit;"><img src="https://hanxinma.gitlab.io/medias/img/202302251677317640784456.png" class="img-shadow img-margin" style="box-sizing:inherit;border-style:none;box-shadow:rgba(0, 0, 0, 0.2) 0px 5px 25px 0px, rgba(0, 0, 0, 0.6) 0px 10px 30px -11px;margin:25px auto 10px;max-width:100%;height:auto;cursor:pointer;"></div><p></p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">我=pg</p><p style="box-sizing:inherit;font-size:1.05rem;line-height:1.85rem;margin:2px 2px 10px;">这样的就是手心的辅助码格式</p></details>',8)]))}const d=e(r,[["render",a]]);export{l as __pageData,d as default};
