import{_ as e,k as t,p as o,ac as E}from"./chunks/framework.DA7r4KNg.js";const s=JSON.parse('{"title":"易学码规则说明","description":"","frontmatter":{},"headers":[],"relativePath":"yixue/dafa.md","filePath":"yixue/dafa.md"}'),d={name:"yixue/dafa.md"};function r(l,a,i,c,n,h){return o(),t("div",null,a[0]||(a[0]=[E('<h1 id="%E6%98%93%E5%AD%A6%E7%A0%81%E8%A7%84%E5%88%99%E8%AF%B4%E6%98%8E" tabindex="-1">易学码规则说明 <a class="header-anchor" href="#易学码规则说明" aria-label="Permalink to &quot;易学码规则说明&quot;">​</a></h1><h2 id="%E5%9F%BA%E6%9C%AC%E6%89%93%E6%B3%95%E8%A7%84%E5%88%99" tabindex="-1">基本打法规则 <a class="header-anchor" href="#基本打法规则" aria-label="Permalink to &quot;基本打法规则&quot;">​</a></h2><ul><li><p><strong>单/双辅助码原则</strong>：</p><ul><li>一字一般用1个辅助码，不行则用2个辅助码</li><li>例：<code>锝</code>→<code>dej</code>（单辅），<code>dejcc</code>（双辅，第二码需重复）</li><li>双辅第二码必须按两次，避免与词重码</li></ul></li><li><p><strong>取码优先级</strong>：</p><ol><li>优先取首码（大字优先）</li><li>首码难取时→改取尾码</li><li>尾码难取时→改取笔画</li></ol><ul><li><em>例：<code>右</code>→先取尾码<code>口</code>，再取<code>一</code>（不取<code>十</code>）</em></li></ul></li></ul><h2 id="%E7%89%B9%E6%AE%8A%E7%BB%93%E6%9E%84%E5%A4%84%E7%90%86%E8%A7%84%E5%88%99" tabindex="-1">特殊结构处理规则 <a class="header-anchor" href="#特殊结构处理规则" aria-label="Permalink to &quot;特殊结构处理规则&quot;">​</a></h2><table tabindex="0"><thead><tr><th>结构类型</th><th>取码规则</th><th>示例</th></tr></thead><tbody><tr><td>含戈包围结构</td><td>先取被包围部分的尾部大字</td><td><code>戴</code>→先取<code>共</code></td></tr><tr><td>类戈字</td><td>均视为戈结构（钱/式尾码为戈）</td><td><code>钱</code>尾码取<code>戈</code></td></tr><tr><td>边类型字</td><td>先左后右取码</td><td><code>遍</code>→先取<code>辶</code></td></tr><tr><td>冷僻/无名字根</td><td>直接跳过不取</td><td><code>隹</code>/<code>爫</code>不取</td></tr></tbody></table><h2 id="%E5%AD%97%E6%A0%B9%E7%AE%80%E5%8C%96%E5%8E%9F%E5%88%99" tabindex="-1">字根简化原则 <a class="header-anchor" href="#字根简化原则" aria-label="Permalink to &quot;字根简化原则&quot;">​</a></h2><ul><li><strong>禁用字根</strong>：<br><code>爫隹疋</code>等冷僻字根不参与编码</li><li><strong>部首通配规则</strong>： 亻→人 犭→犬 讠→言（y键） 足旁→足 日→r键 其他易认部首类推</li></ul><h2 id="%E6%A0%B8%E5%BF%83%E8%AE%BE%E8%AE%A1%E7%89%B9%E7%82%B9" tabindex="-1">核心设计特点 <a class="header-anchor" href="#核心设计特点" aria-label="Permalink to &quot;核心设计特点&quot;">​</a></h2><ol><li><strong>首码优先</strong>：能取首绝不先取尾，降低判断难度</li><li><strong>避用笔画</strong>：通过首尾码组合减少笔画输入需求</li><li><strong>极简字库</strong>：平均每键仅1个字根，冷僻字根全排除</li><li><strong>实时查询</strong>：<code>拼音+oopp</code>可查生字编码</li><li><strong>平滑升级</strong>：后期可无缝过渡到汉心码（重码更少）</li></ol><h2 id="%E5%BF%AB%E9%80%9F%E8%AE%B0%E5%BF%86%E5%8F%A3%E8%AF%80" tabindex="-1">快速记忆口诀 <a class="header-anchor" href="#快速记忆口诀" aria-label="Permalink to &quot;快速记忆口诀&quot;">​</a></h2><h3 id="%E9%A6%96%E7%A0%81%E5%A4%A7%E5%AD%97%E4%BC%98%E5%85%88%E8%B5%B0%EF%BC%8C%E9%9A%BE%E5%8F%96%E5%B0%B1%E6%89%BE%E5%B0%BE%E7%A0%81%E5%87%91" tabindex="-1">首码大字优先走，难取就找尾码凑 <a class="header-anchor" href="#首码大字优先走，难取就找尾码凑" aria-label="Permalink to &quot;首码大字优先走，难取就找尾码凑&quot;">​</a></h3><h3 id="%E6%88%88%E5%8C%85%E5%85%88%E5%8F%96%E9%87%8C%E8%BE%B9%E5%B0%BE%EF%BC%8C%E8%BE%B9%E5%AD%97%E5%B7%A6%E5%8F%B3%E6%8C%89%E9%A1%BA%E5%BA%8F" tabindex="-1">戈包先取里边尾，边字左右按顺序 <a class="header-anchor" href="#戈包先取里边尾，边字左右按顺序" aria-label="Permalink to &quot;戈包先取里边尾，边字左右按顺序&quot;">​</a></h3><h3 id="%E7%94%9F%E5%83%BB%E5%AD%97%E6%A0%B9%E5%85%A8%E8%B7%B3%E8%BF%87%EF%BC%8C%E9%83%A8%E9%A6%96%E9%80%9A%E9%85%8D%E4%B8%8D%E7%94%A8%E6%84%81" tabindex="-1">生僻字根全跳过，部首通配不用愁 <a class="header-anchor" href="#生僻字根全跳过，部首通配不用愁" aria-label="Permalink to &quot;生僻字根全跳过，部首通配不用愁&quot;">​</a></h3><h3 id="%E4%B8%8D%E4%BC%9A%E5%B0%B1%E6%89%93oopp%EF%BC%8C%E6%98%93%E5%AD%A6%E6%B1%89%E5%BF%83%E5%A5%BD%E9%98%9F%E5%8F%8B" tabindex="-1">不会就打oopp，易学汉心好队友 <a class="header-anchor" href="#不会就打oopp，易学汉心好队友" aria-label="Permalink to &quot;不会就打oopp，易学汉心好队友&quot;">​</a></h3>',14)]))}const A=e(d,[["render",r]]);export{s as __pageData,A as default};
